import React from "react";
import './style.css';
import { useState, useEffect } from "react";



function Operator() {
  const [lançar, setLançar] = useState('');

  const [saveValue, setSaveValue] = useState([

  ]);
const handleLocalStorage = () => {

    if (lançar.trim() !== ''){
      const newValue = [...saveValue, lançar]
      setSaveValue(newValue)

      localStorage.setItem('cleinte',  JSON.stringify(newValue))
      setLançar('')
    }

}
const handleDelete = ( id )=>{
  const newSaveValue = saveValue.filter((_,i)=> i !== id)
  setSaveValue(newSaveValue)
  localStorage.setItem('cleinte',  JSON.stringify(newSaveValue))


}
  
useEffect (()=>{
  handleLocalStorage()
},[])

 /*function handleLancar(e){
    e.preventDefault();
    alert('TESTE');
  }
  
   function handleRegister(e){
   e.preventDefault();
    setLançar('')
    setSaveValue([...saveValue, lançar])
    

  } */





  return (
    <div className="containerfundo">
      <div className="painelcontainer">
        <div className="divpainel">
        <section className="lançador">
          
          <h1>PAINEL DE CONTROLE</h1>
       
            <label>Preparando Pedido</label>
          
            <input 
            placeholder="digite o nome do pedido"
            value={lançar}
            nome=""
            onChange={ (e) => setLançar(e.target.value)}/>
            
            <button onClick={handleLocalStorage}>Lançar</button>
          
          </section>
        
        </div>
        <div>
        <section className="lisoperator">
            <h1>PREPARANDO PEDIDO</h1>
            <ul className="prepalist">
               {saveValue.map( (nome, id)  => (
                    <li key={id}>{nome}
                    <button onClick={()=>{handleDelete(id)}}>OK!</button></li>
                ))}
            </ul>
          </section>
        </div>
      </div>
    </div>


        );
    
}

export default Operator;
