import React from "react";
import './style.css';
import { useState, useEffect } from "react";


function Operator() {
  const [lançar, setLançar] = useState('');
  const [saveValue, setSaveValue] = useState([]);

  const handleLocalStorage = () => {
    if (lançar.trim() !== '') {
      const newValue = [...saveValue, lançar];
      setSaveValue(newValue);
      localStorage.setItem('cleinte', JSON.stringify(newValue));
      setLançar('');
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleLocalStorage(); // Aciona a função de lançar quando a tecla Enter for pressionada
    }
  };

  const handleDelete = (id) => {
    const nameToRemove = saveValue[id]; // Nome removido
    const newSaveValue = saveValue.filter((_, i) => i !== id);
    setSaveValue(newSaveValue);
    localStorage.setItem('cleinte', JSON.stringify(newSaveValue));
  
    // Armazenar o nome removido no localStorage
    const removedData = localStorage.getItem("removed");
    const removedNames = removedData ? JSON.parse(removedData) : [];
    removedNames.push(nameToRemove);
    localStorage.setItem("removed", JSON.stringify(removedNames));
  
    // Emitir evento para atualizar o componente View
    window.dispatchEvent(new Event('updateRemovedNames'));
  };
  

  useEffect(() => {
    const data = localStorage.getItem("cleinte");
    if (data) setSaveValue(JSON.parse(data));
  }, []);

  return (
    <div className="containerfundo">
      <div className="painelcontainer">
        <div className="divpainel">
          <section className="lançador">
            <h1>PAINEL DE CONTROLE</h1>
            <label>Preparando Pedido</label>
            <input
              placeholder="Digite o nome do pedido"
              value={lançar}
              onKeyDown={handleKeyDown}
              onChange={(e) => setLançar(e.target.value)}
            />
            <button onClick={handleLocalStorage} >Lançar</button>
          </section>
        </div>
        <div>
          <section className="lisoperator">
            <h1>PREPARANDO PEDIDO</h1>
            <ul className="prepalist">
              {saveValue.map((nome, id) => (
                <li className="lista" key={id}>
                  {nome}
                  <button onClick={() => handleDelete(id)}>OK!</button>
                </li>
              ))}
            </ul>
          </section>
        </div>
      </div>
    </div>
  );
}

export default Operator;
