import logo from './logo.png';
import './App.css';
import img_1 from './img_1.png';
import img_2 from './img_2.png';
import img_3 from './img_3.png';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useState, useEffect } from 'react';

function View() {
  const [saveValue, setSaveValue] = useState([]);

  // Carregar dados do localStorage ao montar o componente
  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem('cliente')) || [];
    setSaveValue(savedData);
  }, []);

  // Listener para atualizar os valores sempre que o localStorage mudar
  useEffect(() => {
    const handleStorageChange = () => {
      const updatedData = JSON.parse(localStorage.getItem('cliente')) || [];
      setSaveValue(updatedData);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const settings = {
    dots: true,
    fade: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    waitForAnimate: false,
    autoplay: true,
  };

  return (
    <div>
      <div className="divlogo">
        <img src={logo} className="imglogo" alt="Logo" />
      </div>

      <div className='container_2'>
        <div className='section_2'>
          <h1 className='prepapedido'>PREPARANDO PEDIDO</h1>
          <ul className='lista'>
            {saveValue.map((nome, id) => (
              <li key={id}>{nome}</li>
            ))}
          </ul>
        </div>
      </div>

      <div className='container_1'>
        <div className='section_1'>
          <h1>SEU PEDIDO EST√Å PRONTO</h1>
          <h1 className='nome'>PEDIDO1</h1>
        </div>
      </div>

      <div>
        <div style={{ width: "30%", margin: "0 auto" }} className='container'>
          <Slider {...settings}>
            <div className='divimg1'>
              <img src={img_1} alt="Imagem 1" style={{ width: "100%" }} className='img1' />
            </div>
            <div className='divimg2'>
              <img src={img_2} alt="Imagem 2" style={{ width: "100%" }} className='img2' />
            </div>
            <div className='divimg3'>
              <img src={img_3} alt="Imagem 3" style={{ width: "100%" }} className='img3' />
            </div>
          </Slider>
        </div>
      </div>
    </div>
  );
}

export default View;
