import React, { createContext, useState } from "react";

// Criação do contexto
export const AppContext = createContext();

export function AppProvider({ children }) {
  const [pedidos, setPedidos] = useState([]);

  return (
    <AppContext.Provider value={{ pedidos, setPedidos }}>
      {children}
    </AppContext.Provider>
  );
}
