import React, { useContext, useState } from "react";
import './style.css';
import { AppContext } from '.'; // Importar o contexto

function Operator() {
  const { pedidos, setPedidos } = useContext(AppContext); // Usar o contexto
  const [lançar, setLançar] = useState('');

  const handleLocalStorage = () => {
    if (lançar.trim() !== '') {
      const newPedidos = [...pedidos, lançar];
      setPedidos(newPedidos);
      localStorage.setItem('cleinte', JSON.stringify(newPedidos));
      setLançar('');
    }
  };

  const handleDelete = (id) => {
    const newPedidos = pedidos.filter((_, i) => i !== id);
    setPedidos(newPedidos);
    localStorage.setItem('cleinte', JSON.stringify(newPedidos));
  };

  return (
    <div className="containerfundo">
      <div className="painelcontainer">
        <div className="divpainel">
          <section className="lançador">
            <h1>PAINEL DE CONTROLE</h1>
            <label>Preparando Pedido</label>
            <input
              placeholder="Digite o nome do pedido"
              value={lançar}
              onChange={(e) => setLançar(e.target.value)}
            />
            <button onClick={handleLocalStorage}>Lançar</button>
          </section>
        </div>
        <div>
          <section className="lisoperator">
            <h1>PREPARANDO PEDIDO</h1>
            <ul className="prepalist">
              {pedidos.map((nome, id) => (
                <li key={id}>
                  {nome}
                  <button onClick={() => handleDelete(id)}>OK!</button>
                </li>
              ))}
            </ul>
          </section>
        </div>
      </div>
    </div>
  );
}

export default Operator;
