import logo from './logo.png';
import './App.css';
import img_1 from './img_1.png';
import img_2 from './img_2.png';
import img_3 from './img_3.png';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useState, useEffect } from 'react';

function View() {
  const [pedidos, setPedidos] = useState([]);
  const [saveValue, setSaveValue] = useState([]);

  const settings = {
    dots: true,
    fade: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    waitForAnimate: false,
    autoplay: true,
  };

  const fetchData = () => {
    const data = localStorage.getItem("cleinte");
    if (data) {
      setSaveValue(JSON.parse(data));
    }
  };

  // Atualiza os dados ao carregar o componente
  useEffect(() => {
    fetchData();

    // Adiciona um listener para detectar mudanças no localStorage
    const handleStorageChange = () => fetchData();
    window.addEventListener("storage", handleStorageChange);

    // Limpa o listener ao desmontar o componente
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  useEffect(() => {
    // Busca os dados do localStorage
    const storedPedidos = localStorage.getItem('cleinte');
    if (storedPedidos) {
      setPedidos(JSON.parse(storedPedidos));
    }
  }, []);

  return (
    <div>
      <div className="divlogo">
        <img src={logo} className="imglogo" alt="Logo" />
      </div>

      <div className="container_2">
        <div className="section_2">
          <h1 className="prepapedido">PREPARANDO PEDIDO</h1>
          <ul className="lista">
            {pedidos.map((pedido, index) => (
              <li key={index}>{pedido}</li>
            ))}
          </ul>
        </div>
      </div>

      <div className="container_1">
        <div className="section_1">
          <h1>SEU PEDIDO ESTÁ PRONTO</h1>
          <h1 className="nome">LUCAS</h1>
        </div>
      </div>

      <div>
        <div style={{ width: "30%", margin: "0 auto" }} className="container">
          <Slider {...settings}>
            <div className="divimg1">
              <img src={img_1} alt="Imagem 1" style={{ width: "100%" }} className="img1" />
            </div>
            <div className="divimg2">
              <img src={img_2} alt="Imagem 2" style={{ width: "100%" }} className="img2" />
            </div>
            <div className="divimg3">
              <img src={img_3} alt="Imagem 3" style={{ width: "100%" }} className="img3" />
            </div>
          </Slider>
        </div>
      </div>
    </div>
  );
}

export default View;
